mapper.select:
SELECT apikey, result, message, createAt, type, mseq
FROM (
	SELECT *
	FROM MFD_LOG
	WHERE TYPE='info'
) AS ML
LEFT OUTER JOIN (
	SELECT *
	FROM INFO
	WHERE ID=#{user_id}
) AS I
ON I.ISEQ = ML.MSEQ
WHERE I.ISEQ != 0 OR APIKEY = #{apikey}
UNION ALL
SELECT apikey, result, message, createAt, type, mseq
FROM (
	SELECT *
	FROM MFD_LOG
	WHERE TYPE='frame'
) AS ML
LEFT OUTER JOIN (
	SELECT *
	FROM FRAME
	WHERE ID=#{user_id}
) AS F
ON F.FSEQ = ML.MSEQ
WHERE F.FSEQ != 0 OR APIKEY = #{apikey}
ORDER BY createAt DESC LIMIT #{start}, #{limit}
