mapper.select:
SELECT *
FROM MFD_TRANSFER
WHERE REQUSER=#{requser}
OR RESUSER=#{resuser}
ORDER BY createAt DESC LIMIT #{start}, #{limit}

SELECT *
FROM MFD_TRANSFER
WHERE REQUSER=#{requser}
ORDER BY createAt DESC LIMIT #{limit}

SELECT *
FROM MFD_TRANSFER
WHERE RESUSER=#{resuser}
ORDER BY createAt DESC LIMIT #{limit}

mapper.update:
UPDATE MFD_TRANSFER
SET RESMSG=#{resmsg}
, STATUS='거절'
WHERE SEQ=#{seq}

UPDATE MFD_TRANSFER
SET RESMSG=#{resmsg}
, STATUS='수락'
WHERE SEQ=#{seq}
